import axios from 'axios'

export function listarComentarios(token){
    return axios({
        method: "GET",
        url: "http://localhost:8393/api/comentarios",
        headers: {
            "token": token
        }
    })
}

export function cadastrarComentario(comentario){
    return axios({
        method: "POST",
        url: "http://localhost:8393/api/comentarios",
        data: comentario
    })
}
