import { useContext, useEffect, useState } from 'react'

import { listarPosts } from '../../../api/postAPI'
import '../PaginaPrincipal/PaginaPrincipal.css'
import Post from '../Postagens/Post'

function LinhaDoTempo(props) {
    const {auth} = useState(AudioContext)
    const [posts, setPosts] = useState([])

    useEffect(() => {
        listarPosts(auth.token).then(
            (response) => {
                setPosts(response.data)
            }
        ).catch(
            (error => {
                console.log(error)
            })
        )
    },[])

    const postagem = posts.map((post) => 
        <Post usuario={post.nomePessoa} texto={post.texto} qtdLikes={post.qtdLikes} comentarios={post.comentarios}>
            {props.comentarios}
        </Post>
    )
    return (
        <div className="linha-tempo">
            {postagem}
        </div>
    )
}

export default LinhaDoTempo
