import '../PaginaPostar/PaginaPostar.css'
import history from '../../../history'

import { NavLink } from 'react-router-dom'
import { useForm } from 'react-hook-form'
import { cadastrarComentario } from '../../../api/comentariosAPI'
import { useState } from 'react'

export function FormPostar() {
    const { register, handleSubmit } = useForm()

    const submeter = (comentario) => {
        cadastrarComentario(comentario).then((response) => {
            console.log(response)
            history.push("/")
        }).catch((error) => {
            console.log(error)
        })
    }
    return (
        <div className="container-form">
            <form onSubmit={handleSubmit(submeter)}>
                Nome: <input type="text" name="usuario" ref={register} />
                <textarea
                    name="texto"
                    cols="30"
                    rows="5"
                    ref={register}
                >
                </textarea>

                <br />

                <NavLink
                    exact
                    to="/"
                >
                    <button type="submit">
                        Postar
                    </button>
                </NavLink>
            </form>
        </div>
    )
}
