import axios from 'axios'

export function listarComentarios(id,token){
    return axios({
        method: "GET",
        url: "http://localhost:8393/api/posts/"+id+"/comentarios",
        headers: {
            "token": token
        }
    })
}

export function cadastrarComentario(token,comentario){
    return axios({
        method: "POST",
        url: "http://localhost:8393/api/comentarios",
        headers: {
            "token": token
        },
        data: comentario
    })
}
