import { useContext, useEffect, useState } from 'react'
import { useForm } from 'react-hook-form'
import { listarComentarios } from '../../../api/postAPI'
import { AuthContext } from '../../../App'
import { Comentario } from '../Comentarios/Comentario'

import '../PaginaPrincipal/PaginaPrincipal.css'

function FormularioComentario({onSubmeter}){
    const {auth,handleSubmit } = useForm()
    const submeter = (comentario) => { onSubmeter(comentario) }
}

export function Postagem(props) {
    const [count, setCount] = useState(0)

    const { auth } = useContext(AuthContext)
    const [comentarios, setComentarios] = useState([])

    useEffect(() => {
        listarComentarios(auth.token).then(
            (response) => {
                setComentarios(response.data)
            }
        ).catch(
            (error => {
                console.log(error)
            })
        )
    }, [auth.token])

    const clicouBotao = () => {
        setCount(count + 1)
    }

    const comentario = comentarios.map((comentario) =>
        <Comentario
            usuario={comentario.usuario}
            texto={comentario.texto}
        >

        </Comentario>
    )
    return (
        <div className="postagens">
            <div className="card">
                <div className="cabecalho">
                    <img src="/user.png" alt="" />
                    <span>{props.usuario}</span>
                </div>

                <hr />

                <div className="card-texto">
                    <p>{props.texto}</p>
                </div>

                <div className="interacoes">
                    <div className="likes">
                        {props.qtdLikes}
                        <span>{count} likes</span>
                    </div>
                    <button onClick={clicouBotao}>
                        <img src="/like.png" alt="" />
                    </button>
                </div>
                <hr />
                <div className="comentarios">
                    {comentario}
                    <textarea
                        name="texto"
                        id=""
                        rows="2"
                    >

                    </textarea>
                    <br />
                    <button type="submit">Comentar</button>
                </div>
            </div>
        </div>

    )
}

export default Postagem
