import { useForm } from 'react-hook-form'

import {cadastrarComentario} from '../../../api/comentariosAPI'

export function Comentario(props) {
    const {register, handleSubmit} = useForm()
    const submeter = (comentario) => {
        cadastrarComentario(comentario).then((response)=>{
            console.log(response)
        }).catch((error)=>{
            console.log(error)
        })
    }

    return (
        <div className="comentarios">
            <form onSubmit={handleSubmit(submeter)} className="card-comentarios">

            </form>

                <div className="cabecalho-comentarios">
                    <img src="/user.png" alt="" />
                    <span>{props.usuario}</span>
                </div>
                <p>{props.texto}</p>

            </div>
    )
}
