import { useForm } from 'react-hook-form'

import {cadastrarComentario} from '../../../api/comentariosAPI'
import '../PaginaPrincipal/PaginaPrincipal.css'

export function Comentario(props) {
    const {register, handleSubmit} = useForm()
    const submeter = (comentario) => {
        cadastrarComentario(comentario).then((response)=>{
            console.log(response)
        }).catch((error)=>{
            console.log(error)
        })
    }

    return (
        <div className="comentarios">
            <form onSubmit={handleSubmit(submeter)}
             className="card-comentarios">
            <div className="cabecalho-comentarios">
                    <img src="/user.png" alt="" />
                    <span ref={register}>{props.usuario}</span>
                    <textarea name="texto" id="" cols="30" rows="10" ref={register}></textarea>
                </div>
            </form>
            </div>
    )
}
